glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in float a_dist;
out float v_dist;
void main () {
  vec4 pos = vec4(a_position, 1);
  pos = cc_matViewProj * cc_matWorld * pos;
  v_color = a_color;
  v_dist = a_dist;
  gl_Position = pos;
}

glsl3.frag:
#if CC_SUPPORT_standard_derivatives
  #extension GL_OES_standard_derivatives : enable
#endif
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in float v_dist;
void main () {
  vec4 o = v_color;
  ALPHA_TEST(o);
  #if CC_SUPPORT_standard_derivatives
    float aa = fwidth(v_dist);
  #else
    float aa = 0.05;
  #endif
  float alpha = 1. - smoothstep(-aa, 0., abs(v_dist) - 1.0);
  o.rgb *= o.a;
  o *= alpha;
  gl_FragColor = o;
}

